import Login from "./Login";
import { useState } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';




const Registration = () => {

    const [flag, setFlag] = useState(false);
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [login, setLogin] = useState("true");
    const [phone, setPhone] = useState("");

    const onSubmit = (e) => {
        e.preventDefault();

        if (!name || !email || !password || !phone) {
            setFlag(true);
        }
        else {
            setFlag(false);

            localStorage.setItem({ email:email, password :password});
            // window.localStorage.setItem("email",JSON.stringify(email));
            // window.localStorage.setItem("password",JSON.stringify(password));
            // let b = [];

            // let a = b.push({ email:"email", password :"password"})
            // console.log(b);
            // console.log(a);
            // localStorage.setItem("login_creds", JSON.stringify(a));
            // console.log("saved");
            // setLogin(!Login);
            <Alert>Successfully Registred</Alert>

        }
    }


    const handleClick = () => {
        setLogin(!login);

    }
    return (
        <>
            <div className="container mt-3">
                {" "}
                {login ? (

                    <Form onSubmit={onSubmit}>
                        <h1 className="text-center mt-5">Registration</h1>

                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Label>Username</Form.Label>
                            <Form.Control type="username" placeholder="Enter Username"
                                onChange={(e) => setName(e.target.value)} />

                        </Form.Group>
                        <Form.Group className="mb-3" controlId="Email">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="email" placeholder="Enter email" onChange={(e) => setEmail(e.target.value)} />

                        </Form.Group>

                        <Form.Group className="mb-3" controlId="Password">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="formBasicPassword">
                            <Form.Label>Phone no.</Form.Label>
                            <Form.Control type="phone" placeholder="Enter phone no." onChange={(e) => setPhone(e.target.value)} />
                        </Form.Group>

                        <Button variant="dark" type="submit">
                            Submit
                        </Button>

                        <p onClick={handleClick} className="text-center " style={{ marginLeft: "31rem", cursor: "pointer" }}>
                            Already registered{" "}log in?

                        </p>
                        {flag && (
                            <Alert color="primary" variant="danger">
                                Every Field is important !
                            </Alert>)}
                    </Form>
                ) : (
                    <Login />
                )}
            </div>
        </>
    )
}

export default Registration;
